var tnt_theme = function () {

	var height = 45;
	var color = "steelblue";
	var threshold;


	//Start theme
	var theme = function(ta, div) {

		//TREE SIDE
		//START
		var newick = "(((((homo_sapiens:9,pan_troglodytes:9)207598:34,callithrix_jacchus:43)314293:52,mus_musculus:95)314146:215,taeniopygia_guttata:310)32524:107,danio_rerio:417)117571:135;";

		//Newick Input
		d3.select(div).append("em").text("Input Tree (Newick): ");

		var inputform = d3.select(div)
							.append("input");

		var button_enter = d3.select(div)
							.append("button")
							.text("Enter")
							.on("click", function() {

								newick = inputform.node().value;

								ta.data (biojs.vis.tree.parse_newick(newick));
								ta.update();

							});

		//Scaling and unscaling of the tree
		var button_scale = d3.select(div)
							.append("button")
							.text("Scale")
							.on("click", function() {
								button_scale.attr("hidden","hidden")
								button_unscale.attr("hidden",null)

								ta.layout (biojs.vis.tree.tree.layout.vertical()
									.width(430)
									.scale(true)
								);
								ta.update();
							});

		var button_unscale = d3.select(div)
								.append("button")
								.text("Unscale")
								.attr("hidden","hidden")
								.on("click", function() {
								button_unscale.attr("hidden","hidden")
								button_scale.attr("hidden",null)


								ta.layout (biojs.vis.tree.tree.layout.vertical()
										.width(430)
										.scale(false)
									);
									ta.update();
							    });

		//Configure the tree
		ta
			.data (biojs.vis.tree.parse_newick(newick))
			.layout (biojs.vis.tree.tree.layout.vertical()
				.width(430)
				.scale(false)
				)
			.label (biojs.vis.tree.tree.label.text()
					.text(function (node) {
						return node.data().name;
					})
					.fontsize(13)
					.height(height)
			       );

		//collapse nodes on dbl click
		ta.on_dbl_click (function(node){
			node.toggle();
			ta.update();
		})

		
		ta(div);

	};
	//End theme

	//Now we could add API methods like theme.method....

	return theme;

};

